def seed_categories(q):
	templ = "دسته بندی "
	q = round(q/10)
	cat = ""
	for i in range(0,q):
		cat = cat + f'{i}:{templ}{i}\n'

	f = open('categories.txt',"w")
	f.write(cat[:-1])
	f.close()
	return 1


